INSERT INTO `inicio` (`id`, `img`, `titulo`, `subtitulo`, `texto1`, `texto2`) VALUES
(1, 'https://www.peru.travel/Contenido/TipoExperiencia/Imagen/pe/27/1.1/Principal/Historico%20Cultural.jpg', 'Histórico Cultural\r\n', '¿Cuál Es El Exponente Más Sorprendente Del Pasado En América Latina?\r\n', 'La respuesta inmediata sería: Machu Picchu… y sí, claro que lo es, pero nosotros te decimos: hay más. Por todo el Perú podrás encontrar vestigios maravillosos que sorprenden al mundo, Machu Picchu es un must, pero también lo son Kuélap y Chavín de Huántar, Caral y Chan Chan, las Líneas de Nasca y Sipán… y seguiríamos enumerando!', NULL),
(2, 'https://www.peru.travel/Contenido/TipoExperiencia/Imagen/pe/28/1.1/Principal/Aventura.jpg', 'Aventura', '¿Has querido alguna vez llevar tu emoción al límite?\r\n', 'Imagínate esto: un desierto costero, luego unas montañas tan altas que tocan el cielo, después una selva tupida y húmeda... y el mar, el mar peruano, intenso y calmado. Hermoso. En cada escenario, una emoción distinta saldrá a tu encuentro.', NULL),
(3, 'https://www.peru.travel/Contenido/TipoExperiencia/Imagen/pe/32/1.1/Principal/Naturaleza.jpg', 'Naturaleza\r\n', '¿Tienes idea de lo que la naturaleza peruana te ofrece?', 'Te lo diremos en pocas palabras: en Perú podrás encontrar más de 1 300 especies de aves, 200 especies de mamíferos, 1 200 de mariposas y más de 10 000 formas diferentes de árboles... y no estamos contando todo.', NULL);

